INSERT INTO `tb_config` (`id`, `setting`, `update_time`, `create_time`) VALUES
(1, 'a:34:{s:5:"title";s:23:"XX网络科技工作室";s:4:"logo";s:45:"20190703/009c811106fc44ccabce56a516582b9d.jpg";s:2:"qq";s:5:"11111";s:6:"wechat";s:13:"www.riyao.net";s:9:"copyright";s:20:"闽ICP备15018207号";s:10:"baidu_stat";s:0:"";s:10:"baidu_chat";s:0:"";s:9:"seo_title";s:92:"【秒赚】人人帮-微信投票|微信助力|APP下载|游戏推广|营销推广任务墙";s:12:"seo_keywords";s:80:"人人帮,微信投票,微信助力,APP下载,游戏推广,营销推广任务墙";s:15:"seo_description";s:183:"人人帮推广是一站式的移动端任务式赚钱营销推广服务平台，提供微信拉票、微信助力、朋友圈转发、APP下载推广、游戏网贷理财推广等！";s:9:"SMTP_HOST";s:0:"";s:9:"SMTP_PORT";s:0:"";s:9:"SMTP_USER";s:0:"";s:9:"SMTP_PASS";s:0:"";s:10:"FROM_EMAIL";s:0:"";s:9:"FROM_NAME";s:6:"系统";s:10:"TEST_EMAIL";s:0:"";s:27:"invitation_first_task_award";s:3:"0.1";s:26:"invitation_withdraw_rebate";s:1:"0";s:17:"invitation_rebate";s:2:"10";s:17:"sign_give_credit1";s:2:"20";s:18:"sign_continue_give";s:1:"3";s:12:"withdraw_min";s:1:"5";s:12:"withdraw_fee";s:1:"5";s:3:"fee";s:2:"10";s:6:"period";s:9:"0#6#12#24";s:5:"speed";s:1:"6";s:10:"push_check";s:1:"1";s:7:"top_fee";s:1:"1";s:12:"top_max_hour";s:2:"12";s:24:"receive_order_limit_time";s:1:"5";s:22:"check_order_limit_time";s:1:"2";s:16:"join_task_detail";s:1372:"<h5>1、点击一个任务。</h5><p><img alt="1517191346729689.png" src="/ueditor/php/upload/image/20180129/1517191346729689.png"/></p><h5>2、浏览完任务需求后点击 “抢单” 按键，若跳转到做任务界面则抢单成功，如果失败则是目前单数已满。请在抢单后的两小时内完成任务，逾期则自动解锁。</h5><p><img title="1517191382134000.png" alt="help2.png" src="/ueditor/php/upload/image/20180129/1517191382134000.png"/></p><h5>3、跳转到做任务页面，在文本框写上任务需要的文字内容，如果任务有要求的话，还需要上传相应的图片，一切都弄好之后点击右上角的发布，静待文件上传成功后自动跳转。</h5><p><img title="1517191394899149.png" alt="help3.png" src="/ueditor/php/upload/image/20180129/1517191394899149.png"/></p><h5>4、如果抢单后发现自己不符合任务需求，则退回任务详情页，点击解锁即可，如果不点击而又不在两小时内上传任务相关内容的话，系统将会自动解锁。</h5><p><img title="1517191401639366.png" alt="help4.png" src="/ueditor/php/upload/image/20180129/1517191401639366.png"/></p><h5>5、做过的、正在做的任务可以在个人中心的参与中查看。</h5><p><img title="1517191411866530.png" alt="help5.png" src="/ueditor/php/upload/image/20180129/1517191411866530.png"/></p>";s:16:"push_task_detail";s:1599:"<h5>1、点击页面下方发布按键。</h5><p><img title="1517191633304320.png" alt="help6.png" src="/ueditor/php/upload/image/20180129/1517191633304320.png"/></p><h5>2、根据发布任务的要求填写相关内容。</h5><p><img title="1517191637329752.png" alt="help7.png" src="/ueditor/php/upload/image/20180129/1517191637329752.png"/></p><h5>3、点击右上角的发布。如果页面跳转则发布成功，若发布失败，则需根据弹出的错误信息进行相应的修改。</h5><p><img title="1517191642115923.png" alt="help8.png" src="/ueditor/php/upload/image/20180129/1517191642115923.png"/></p><h5>4、发布过的任务可以在“发布”中查看。</h5><p><img title="1517191647505020.png" alt="help9.png" src="/ueditor/php/upload/image/20180129/1517191647505020.png"/></p><h5>5、点击在进行中的任务。</h5><p><img title="1517191652117756.png" alt="help10.png" src="/ueditor/php/upload/image/20180129/1517191652117756.png"/></p><h5>6、在发布任务的任务详情里顶部的审核按键或者直接在底部皆可审核任务。</h5><p><img title="1517191660163219.png" alt="help11.png" src="/ueditor/php/upload/image/20180129/1517191660163219.png"/></p><h5>7、如果审核不通过的话要写明不通过的理由，方便推手进行修改。</h5><p><img title="1517191670623745.png" alt="help12.png" src="/ueditor/php/upload/image/20180129/1517191670623745.png"/></p><h5>8、在任务详情里还可以进行额外的操作。</h5><p><img title="1517191675786750.png" alt="help13.png" src="/ueditor/php/upload/image/20180129/1517191675786750.png"/></p>";}', 1581344276, 1516952068);
